pragma solidity ^0.4.25;

contract HealthRecord {
    // Model a record
    struct Record {
        string diagnos_with;
        uint priority;
        string doctor_id;
        string confirm_id;
    }

    mapping(uint => Record) public records;

    uint public recordCount;

    //voted event
    event votedEvent (
        uint indexed _candidateId
    );

    function addRecord (string _name, uint _priority, string _doctor_id, string _confirm_id) public {
        recordCount ++;
        records[recordCount] = Record(_name, _priority, _doctor_id, _confirm_id);

    }
}
